mapper.update:
UPDATE animal_profiles
name = #{name}, species = #{species}, breed = #{breed}, gender = #{gender}, age = #{age}, health_status = #{healthStatus}, description = #{description}, image_url = #{imageUrl}, status = #{status}, updated_at = NOW()
WHERE id = #{id}

UPDATE animal_profiles
SET status = #{status}
WHERE id = #{animalId}

UPDATE animal_profiles
SET status = #{newStatus}
WHERE id = #{animalId}
  AND status = #{expectedStatus}

mapper.select:
SELECT ap.id, ap.shelter_id AS shelterId, ap.name, ap.species, ap.breed, ap.gender, ap.age, ap.health_status AS healthStatus, ap.description, ap.image_url AS imageUrl, ap.status, CASE WHEN ap.status = 0 THEN '待审核' WHEN ap.status = 1 THEN '可领养' WHEN ap.status = 2 THEN '已领养' WHEN ap.status = 3 THEN '审核不通过' ELSE '未知状态' END AS status_str, ap.created_at AS createdAt, ap.updated_at AS updatedAt, s.shelter_name AS shelterName, s.contact_person AS contactPerson FROM animal_profiles ap JOIN shelters s ON ap.shelter_id = s.id
AND ap.id = #{animalProfile.id}
AND s.shelter_name LIKE CONCAT('%', #{animalProfile.shelterName}, '%')
AND ap.status = #{animalProfile.status}
LIMIT #{offset}, #{limit}
