mapper.update:
UPDATE animal_profiles
name = #{name}, species = #{species}, breed = #{breed}, gender = #{gender,com.example.animal_shelet.TypeHandler.Animal.AnimalProfile.GenderTypeHandler}, age = #{age}, health_status = #{healthStatus}, description = #{description}, image_url = #{imageUrl}, status = #{status,typeHandler=com.example.animal_shelet.TypeHandler.Animal.AnimalProfile.AnimalStatusTypeHandler}, updated_at = NOW()
WHERE id = #{id}

UPDATE animal_profiles
SET status = #{status}
WHERE id = #{animalId}

mapper.select:
SELECT ap.id, ap.shelter_id AS shelterId, ap.name, ap.species, ap.breed, ap.gender, ap.age, ap.health_status AS healthStatus, ap.description, ap.image_url AS imageUrl, ap.status, ap.created_at AS createdAt, ap.updated_at AS updatedAt, s.shelter_name AS shelterName, s.contact_person AS contactPerson FROM animal_profiles ap JOIN shelters s ON ap.shelter_id = s.id
AND ap.name LIKE CONCAT('%', #{animalProfile.name}, '%')
AND ap.species = #{animalProfile.species}
AND ap.breed LIKE CONCAT('%', #{animalProfile.breed}, '%')
AND ap.gender = #{animalProfile.gender, typeHandler=com.example.animal_shelet.TypeHandler.Animal.AnimalProfile.GenderTypeHandler}
AND ap.age = #{animalProfile.age}
AND ap.health_status = #{animalProfile.healthStatus}
AND ap.status = #{animalProfile.status, typeHandler=com.example.animal_shelet.TypeHandler.Animal.AnimalProfile.AnimalStatusTypeHandler}
AND s.shelter_name LIKE CONCAT('%', #{animalProfile.shelterName}, '%')
AND s.contact_person LIKE CONCAT('%', #{animalProfile.contactPerson}, '%')
AND 1=1
ORDER BY ap.created_at DESC LIMIT #{offset}, #{limit}
